package com.methods;

public class AverageValue {
	
	public static void Average(float a,float b,float c) {
		
		System.out.println((a+b+c)/3);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =25;
		int b=45;
		int c = 65;
		
		Average(a,b,c);
	}

}
